def counting(journalist)  #----------------------ok
      journalistv2 = journalist.map { |handle| handle.gsub("@", "") }
      puts "Nombre de Handle dans la liste est de -----------> #{journalistv2.count}"
end




def short_handle(journalist) #-----------------------ok
    puts "Le Handle le plus court est ------------> #{journalist.min_by(&:length)}"
end




def for_five(journalist) #-----------------ok
    number = 0
    journalist.each do|i|
        if i.length ==6
            number =number+1
        end
    end

        puts "Le nombre de handle ayant 5 caractères est de #{number}"
end



def begin_capital(journalist) #-------------ok
        journalistv2 = journalist.map { |handle| handle.gsub("@", "") }
        count = journalistv2.count {|str| str[0] == str[0].upcase}
        puts "Le nombre de Handle commençant par une majuscule est de #{count}"

end



def sort_alpha(journalist) #-------------------ok
    journalistv2 = journalist.map { |handle| handle.gsub("@", "") }
    journalistv3 = journalistv2.map(&:upcase)
    puts "La liste des Handles par ordre alphabétique est #{journalistv3.sort}\t"
end




def sort_size(journalist) #-----------------ok
    journalistv2 = journalist.map { |handle| handle.gsub("@", "") }
    journalistv3 = journalistv2.map(&:upcase)
    puts "handle du plus petit au plus grand #{journalistv3.sort_by(&:length)}"
end




def position(journalist) #------------------------ok
    result= journalist.index("@epenser")
    puts "La position de l'array @epense est #{result}"
end



