def just_do_it (journalist)
   
    while true

    puts "Hello you, ça farte ?"
    puts "Tu veux connaitre les réponses à notre projet Big Data ?"
    puts "Alors, fais un choix :"
    puts "1 - Connaitre le nombre d'handle"
    puts "2 - Le handle le plus court"
    puts "3 - Le nombre de handle contenant 5 caractères"
    puts "4 - Savoir le nombre de handle commençant par une majuscule"
    puts "5 - Le tri des handles par ordre alphabétique"
    puts "6 - La liste des handle par taille"
    puts "7 - La position du handle @epenser"
    puts "8 - La répartition des handle par taille"
  
    print "Que choisis-tu ? > "
    choix = gets.chomp.to_i

    case choix
    when 1
        counting(journalist)
    when 2
        short_handle(journalist)
    when 3
        for_five(journalist)
    when 4
        begin_capital(journalist)
    when 5
        sort_alpha(journalist)
    when 6
        sort_size(journalist)
    when 7
        position(journalist)
    else
        puts "Choix invalide."
    end

    puts "Veux-tu continuer ? (O/N)"
    continuer = gets.chomp.upcase
    break if continuer != "O"
end

puts "A bientôt ;-)"
end


just_do_it(journalist)








